// SPDX-License-Identifier: <SPDX-License>
pragma solidity 0.6.12;

import {ILendingPool} from "@aave/protocol-v2/contracts/interfaces/ILendingPool.sol";
import {ILendingPoolAddressesProvider} from "@aave/protocol-v2/contracts/interfaces/ILendingPoolAddressesProvider.sol";
import {IWETHGateway} from "@aave/protocol-v2/contracts/misc/interfaces/IWETHGateway.sol";

interface DaiToken {
    function balanceOf(address guy) external view returns (uint);
}

contract BorrowDai {
	DaiToken daitoken;
	address constant WETH = 0xcc9a0B7c43DC2a5F023Bb9b738E45B0Ef6B06E04;
	address constant DAI = 0x6B175474E89094C44Da98b954EedeAC495271d0F;
	address constant pool = 0x7d2768dE32b0b80b7a3454c06BdAc94A69DDc7A9;

	//constructor() public{
	//	daitoken = DaiToken(0x6B175474E89094C44Da98b954EedeAC495271d0F);
	//}

	function borrow(uint256 DAIamount) public returns (bool success) {
		IWETHGateway(WETH).depositETH(msg.sender, 0);
		ILendingPool(pool).borrow(DAI, DAIamount, 1, 0, msg.sender);
	}
}
